import React from 'react';
import './Welcome.css';

const Welcome = () => {
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section id="main-content" className="welcome">
      <div className="container">
        <div className="welcome-content">
          <p className="welcome-subtitle">UW AUTO, ONZE PASSIE</p>
          <h2 className="welcome-title">Welkom bij Premium Car Works in Maastricht.</h2>
          
          <div className="welcome-text">
            <p>
              Dé specialist in high-end detailing, professioneel onderhoud en voertuigpersonalisatie. 
              Bij ons krijgt uw auto de zorg, aandacht en uitstraling die het verdient.
            </p>
            <p>
              Wij zijn geen traditionele garage, maar een moderne en veelzijdige werkplaats waar kwaliteit, 
              innovatie en passie samenkomen. Uw wensen staan centraal, en wij zorgen ervoor dat uw voertuig 
              altijd in topconditie verkeert.
            </p>
          </div>
          
          <p className="welcome-cta-text">
            Ontdek hieronder wat wij voor uw auto kunnen betekenen!
          </p>
          
          <div className="welcome-buttons">
            <button 
              className="welcome-btn"
              onClick={() => scrollToSection('detailing')}
            >
              <span className="btn-icon">→</span>
              Detailing
            </button>
            <button 
              className="welcome-btn"
              onClick={() => scrollToSection('service')}
            >
              <span className="btn-icon">→</span>
              Service
            </button>
            <button 
              className="welcome-btn"
              onClick={() => scrollToSection('customs')}
            >
              <span className="btn-icon">→</span>
              Customs
            </button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Welcome;
