import React from 'react';
import CountUp from 'react-countup';
import { useInView } from 'react-intersection-observer';
import './Statistics.css';

const Statistics = () => {
  const [ref, inView] = useInView({
    threshold: 0.3,
    triggerOnce: true
  });

  const stats = [
    {
      id: 1,
      number: 6,
      suffix: '+',
      label: 'JAREN ERVARING',
      color: 'var(--primary-color)'
    },
    {
      id: 2,
      number: 125,
      suffix: '+',
      label: 'TROUWE KLANTEN',
      color: 'var(--primary-color)'
    },
    {
      id: 3,
      number: 2,
      suffix: '+',
      label: 'TEAM LEDEN',
      color: 'var(--primary-color)'
    },
    {
      id: 4,
      number: 5,
      suffix: '/5',
      label: 'KLANTENBEOORDELING',
      color: 'var(--text-light)',
      background: 'var(--dark-bg)'
    }
  ];

  const socialLinks = [
    {
      id: 1,
      icon: 'ðŸ’¬',
      title: 'WhatsApp',
      subtitle: 'Stuur ons een bericht',
      link: 'https://wa.me/31432340800',
      color: '#25D366'
    },
    {
      id: 2,
      icon: 'ðŸ“˜',
      title: 'Facebook',
      subtitle: 'Bekijk onze pagina',
      link: 'https://facebook.com/premiumcarworks',
      color: '#1877F2'
    },
    {
      id: 3,
      icon: 'ðŸ“·',
      title: 'Instagram',
      subtitle: 'Bekijk onze projecten',
      link: 'https://instagram.com/premiumcarworks',
      color: '#E4405F'
    }
  ];

  const handleSocialClick = (link) => {
    window.open(link, '_blank', 'noopener,noreferrer');
  };

  return (
    <section className="statistics">
      <div className="container">
        {/* Social Media Section */}
        <div className="social-section">
          <div className="social-grid">
            {socialLinks.map((social) => (
              <div 
                key={social.id} 
                className="social-card"
                onClick={() => handleSocialClick(social.link)}
                style={{ '--hover-color': social.color }}
              >
                <div className="social-icon">
                  <span>{social.icon}</span>
                </div>
                <div className="social-content">
                  <h3 className="social-title">{social.title}</h3>
                  <p className="social-subtitle">{social.subtitle}</p>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Statistics Section */}
        <div className="stats-section" ref={ref}>
          <div className="stats-grid">
            {stats.map((stat) => (
              <div key={stat.id} className="stat-card" style={{ background: stat.background }}>
                <div className="stat-number" style={{ color: stat.color }}>
                  {inView && (
                    <CountUp
                      start={0}
                      end={stat.number}
                      duration={2.5}
                      suffix={stat.suffix}
                    />
                  )}
                </div>
                <div className="stat-label" style={{ color: stat.color }}>
                  {stat.label}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Statistics;
