import React from 'react';
import './Service.css';

const Service = () => {
  const services = [
    {
      id: 1,
      title: 'Essential Wash',
      price: 'Vanaf €59,-',
      icon: 'fas fa-car'
    },
    {
      id: 2,
      title: 'Premium Wash',
      price: 'Vanaf €109,-',
      icon: 'fas fa-star'
    },
    {
      id: 3,
      title: 'Wash & Wax',
      price: 'Vanaf €149,-',
      icon: 'fas fa-spray-can'
    },
    {
      id: 4,
      title: 'Booster Wash',
      price: 'Vanaf €159,-',
      icon: 'fas fa-bolt'
    },
    {
      id: 5,
      title: 'Cabrio Dak',
      price: 'Vanaf €39,-',
      icon: 'fas fa-car-side'
    },
    {
      id: 6,
      title: 'Bodem Reiniging',
      price: 'Prijs op aanvraag',
      icon: 'fas fa-car-battery'
    },
    {
      id: 7,
      title: 'Motor compartiment',
      price: 'Vanaf €39,-',
      icon: 'fas fa-cog'
    },
    {
      id: 8,
      title: 'Velgen & Wielkasten',
      price: 'Vanaf €169,-',
      icon: 'fas fa-circle'
    }
  ];

  return (
    <section className="service-page">
      <div className="container">
        <div className="page-header">
          <h1>BETROUWBARE ZORG VOOR OPTIMALE PRESTATIES</h1>
          <h2>Onderhoud & Reparatie voor Uw Voertuig</h2>
          <p>
            Bij Premium Car Works krijgt uw auto de aandacht en zorg die het verdient. Onze Service-afdeling biedt een compleet pakket aan onderhouds- en reparatiediensten, vergelijkbaar met een traditionele autogarage.
          </p>
          <p>
            Dankzij onze ervaring werken wij met hoogwaardige onderdelen en geavanceerde technieken om uw voertuig veilig, betrouwbaar en in topconditie te houden.
          </p>
        </div>

        <div className="service-categories">
          <h3>Onze service categorieën</h3>
          <div className="category-buttons">
            <button className="category-btn active">Aandrijving</button>
            <button className="category-btn">Onderstel</button>
            <button className="category-btn">Carrosserie</button>
            <button className="category-btn">Elektronica</button>
          </div>
        </div>

        <div className="services-section">
          <div className="services-grid">
            {services.map((service) => (
              <div key={service.id} className="service-card">
                <div className="service-icon">
                  <i className={service.icon}></i>
                </div>
                <h3 className="service-title">{service.title}</h3>
                <p className="service-price">{service.price}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Service;
