import React, { useState, useEffect } from 'react';
import './Gallery.css';

const Gallery = () => {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Haal je access token uit environment variables
  const INSTAGRAM_ACCESS_TOKEN = process.env.REACT_APP_INSTAGRAM_ACCESS_TOKEN;

  // Haal de Instagram User ID van de ingelogde gebruiker via de token
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    if (!INSTAGRAM_ACCESS_TOKEN) {
      setError('Instagram Access Token is niet ingesteld.');
      setLoading(false);
      return;
    }

    // Eerst de user ID ophalen en daarna de posts
    const fetchUserIdAndPosts = async () => {
      try {
        setLoading(true);

        // 1. Haal de User ID op
        const userRes = await fetch(`https://graph.instagram.com/me?fields=id,username&access_token=${INSTAGRAM_ACCESS_TOKEN}`);
        if (!userRes.ok) throw new Error('Kon Instagram gebruiker niet ophalen');
        const userData = await userRes.json();
        setUserId(userData.id);

        // 2. Haal posts op van de user
        await fetchInstagramPosts(userData.id);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    // Ophalen van posts
    const fetchInstagramPosts = async (userId) => {
      try {
        const postsRes = await fetch(
          `https://graph.instagram.com/${userId}/media?fields=id,caption,media_type,media_url,thumbnail_url,permalink,timestamp,like_count,comments_count&access_token=${INSTAGRAM_ACCESS_TOKEN}&limit=12`
        );
        if (!postsRes.ok) throw new Error('Kon Instagram posts niet ophalen');
        const postsData = await postsRes.json();

        // Filter alleen afbeeldingen en carousel albums (foto’s)
        const processedPosts = postsData.data
          .filter(post => post.media_type === 'IMAGE' || post.media_type === 'CAROUSEL_ALBUM')
          .map(post => ({
            id: post.id,
            image: post.media_url || post.thumbnail_url,
            caption: post.caption || '',
            likes: post.like_count || 0,
            comments: post.comments_count || 0,
            timestamp: new Date(post.timestamp),
            permalink: post.permalink,
          }));

        setImages(processedPosts);
        setError(null);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchUserIdAndPosts();

    // Auto-refresh elke 5 minuten (300000 ms)
    const interval = setInterval(() => {
      if (userId) {
        fetchInstagramPosts(userId);
      }
    }, 300000);

    return () => clearInterval(interval);

  }, [INSTAGRAM_ACCESS_TOKEN, userId]);

  // Format getallen voor likes/comments
  const formatNumber = (num) => {
    if (num >= 1000000) return (num / 1000000).toFixed(1) + 'M';
    if (num >= 1000) return (num / 1000).toFixed(1) + 'k';
    return num.toString();
  };

  // Format tijdsduur zoals '3u geleden'
  const formatTimeAgo = (timestamp) => {
    const now = new Date();
    const diffInHours = Math.floor((now - timestamp) / (1000 * 60 * 60));
    if (diffInHours < 1) return 'Nu';
    if (diffInHours < 24) return `${diffInHours}u geleden`;
    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) return `${diffInDays}d geleden`;
    const diffInWeeks = Math.floor(diffInDays / 7);
    if (diffInWeeks < 4) return `${diffInWeeks}w geleden`;
    const diffInMonths = Math.floor(diffInDays / 30);
    return `${diffInMonths}m geleden`;
  };

  const handleImageClick = (permalink) => {
    window.open(permalink, '_blank', 'noopener,noreferrer');
  };

  if (loading) {
    return (
      <section className="gallery-page">
        <div className="container">
          <div className="page-header">
            <h1>GALLERIJ</h1>
            <h2>Bekijk onze laatste projecten</h2>
            <p>Volg ons op Instagram voor meer updates en projecten</p>
          </div>
          <div className="loading-spinner">
            <div className="spinner"></div>
            <p>Instagram galerij laden...</p>
          </div>
        </div>
      </section>
    );
  }

  if (error) {
    return (
      <section className="gallery-page">
        <div className="container">
          <div className="page-header">
            <h1>GALLERIJ</h1>
            <h2>Bekijk onze laatste projecten</h2>
            <p>Volg ons op Instagram voor meer updates en projecten</p>
          </div>
          <div className="error-message">
            <p>Fout: {error}</p>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section className="gallery-page">
      <div className="container">
        <div className="page-header">
          <h1>GALLERIJ</h1>
          <h2>Bekijk onze laatste projecten</h2>
          <p>Volg ons op Instagram voor meer updates en projecten</p>
          <a 
            href={`https://instagram.com/${INSTAGRAM_USERNAME}`} 
            target="_blank" 
            rel="noopener noreferrer"
            className="instagram-link"
          >
            <i className="fab fa-instagram"></i> Volg ons op Instagram
          </a>
        </div>

        <div className="gallery-grid">
          {images.map(image => (
            <div
              key={image.id}
              className="gallery-item"
              onClick={() => handleImageClick(image.permalink)}
            >
              <div className="image-container">
                <img src={image.image} alt={image.caption} loading="lazy" />
                <div className="image-overlay">
                  <div className="instagram-stats">
                    <div className="stat">
                      <i className="fas fa-heart"></i> <span>{formatNumber(image.likes)}</span>
                    </div>
                    <div className="stat">
                      <i className="fas fa-comment"></i> <span>{formatNumber(image.comments)}</span>
                    </div>
                  </div>
                  <div className="image-timestamp">{formatTimeAgo(image.timestamp)}</div>
                </div>
              </div>
              <div className="image-caption">
                <p>{image.caption}</p>
              </div>
            </div>
          ))}
        </div>

        <div className="live-updates">
          <p>
            <i className="fas fa-sync-alt"></i> Live updates - Nieuwe posts worden automatisch toegevoegd
          </p>
          <p className="update-info">
            Laatste update: {new Date().toLocaleTimeString('nl-NL')}
          </p>
        </div>
      </div>
    </section>
  );
};

export default Gallery;
